            <div class="text-xs font-semibold uppercase">
                {{ __('Free shipping to your location on orders over', 'sage') }}
                <span
                    class=""
                    x-html="`${freeshipping.free_shipping_amount}{!! get_woocommerce_currency_symbol() !!}`"
                ></span>
            </div>
        </div>

        <div class="">
            <template x-if="freeshipping.enabled">
                <div class="w-full text-xs font-semibold uppercase text-[#229E44]">
                    {{ __('You have Free Shipping!', 'sage') }}
                </div>
            </template>
            <template x-if="!freeshipping.enabled">
                >
                <div class="flex w-full items-center gap-4">
                    <div
                        class="grow-0 text-xs font-semibold uppercase text-body"
                        x-html="'{{ __('Remaining', 'sage') }}'"
                    ></div>
                    <div
                        id="freeShippingPercentageBar"
                        :class="'before:width-['+freeshipping.percentage+'%]'"
                        class="before:content-[\' \'] before:bg-black relative w-full flex-1 shrink-0 before:absolute before:inset-0 before:h-full"
                    >
                        <div class="h-2 bg-slate-100"></div>
                        {{-- <span id="freeShippingPercentageBarHandle" :style="'left:'+freeshipping.percentage+'%;'" class="absolute inset-0 top-1/2 h-4 w-4 -translate-x-1/2 -translate-y-1/2 transform rounded-full bg-primary transition duration-150 ease-in-out"></span> --}}
                    </div>
                    <div
                        class="grow-0 text-xs font-bold text-body"
                        x-html="`${freeshipping.amount_to_free_shipping}{!! get_woocommerce_currency_symbol() !!}`"
                    ></div>
                </div>
            </template>
        </div>
    </div>
</template>
